const homeTestInterval = setInterval(() => {
	try {
		const exist_elm = document.querySelector('#mainContent');
		const head = document.querySelector('head');
		if (exist_elm && head && !document.querySelector('.new-elm')) {
			head.insertAdjacentHTML('beforeend', style);
			exist_elm.insertAdjacentHTML('afterbegin', new_elm_html);
			clearInterval(homeTestInterval);
		}
	} catch (error) {
		console.log('error', error);
	}
}, 10);
